import React, { useState } from 'react';
import styled from 'styled-components';
import { breakpoints, colors } from '../../styles';
import Title from '../Title';
import InfoBoxWithMenu from './InfoBoxWithMenu';
import { Bounce } from 'react-reveal';
import img2 from '../../assets/img2.jpg';
import img3 from '../../assets/img3.jpg';
import Paragraph from './Paragraph';

const Box = styled.div`
  height: ${(props) => (!props.isOpened === true ? '0' : '500px')};
  overflow-y: ${(props) => (!props.isOpened ? 'hidden' : 'scroll')};
  transition: all 0.5s 0.1s;
  width: 100%;
  margin: 0 auto;

  @media ${breakpoints('lg')} {
    width: 90%;
  }
`;

const About = styled.div`
  width: 100%;
  position: relative;

  @media ${breakpoints('md')} {
    display: flex;
    flex-direction: row-reverse;
    align-items: center;
  }
`;

const AboutContentBox = styled.div`
  position: relative;

  @media ${breakpoints('md')} {
    > div {
      /* position: absolute;
      transform: rotate(90deg);
      transform-origin: 50% 50%; */
      /* right: 0;
      top: 0; */
    }
  }
`;

const ProjectsContentBox = styled.div``;

const Projects = styled.div`
  @media ${breakpoints('md')} {
    display: flex;
    align-items: center;
  }
`;

const Par = styled.p`
  font-size: 20px;
  line-height: 1.6;
  color: ${colors().navy1};
  padding-bottom: 30px;
`;

const ImagesBox = styled.div`
  display: flex;

  @media ${breakpoints('md')} {
    margin-right: 50px;
    margin-bottom: 20px;

    > div:nth-of-type(2) {
      margin-left: -80px;
      margin-top: 50px;
    }
  }
`;

const Image = styled.div`
  background-image: ${(props) => `url(${props.img})`};
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  width: 200px;
  height: 300px;
  margin: 0 10px;
  box-shadow: 0 0 12px -8px ${colors().navy1};
  transition: all 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

function AboutDetails({ isOpened }) {
  const [state] = useState([
    {
      id: 1,
      name: 'eco masuria',
      text: 'eco masuria bla bla  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.',
    },
    {
      id: 2,
      name: 'sun vibes',
      text: 'sun vibes bla bla bla  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.',
    },
    {
      id: 3,
      name: 'urocze wzg贸rze',
      text: 'urocze wzg贸rze bla bla bla  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.  Lorem ipsum dolor sit amet consectetur adipisicing elit.',
    },
  ]);
  const [active, setActive] = useState(1);

  const handleClick = (e) => {
    setActive(parseInt(e.target.dataset.id));
  };

  const text = state.filter((el) => el.id === active)[0].text;

  return (
    <Box isOpened={isOpened} className="about">
      <Bounce right>
        <About>
          <AboutContentBox>
            <Title
              text="o mnie"
              size={68}
              color={colors(0.2).navy3}
              weight={700}
            />
            <Par>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam
              ipsum, quam exercitationem aliquam consectetur sint placeat, nihil
              aut quas earum, quae officiis! Velit voluptates vel nesciunt eos
              a, eum animi explicabo maxime fuga unde, consectetur facere et
              voluptate officia, nisi praesentium tenetur fugiat. Perferendis
              pariatur nemo ex mollitia iste modi!
            </Par>
          </AboutContentBox>
          <ImagesBox>
            <Image img={img2}></Image>
            <Image img={img3}></Image>
          </ImagesBox>
        </About>

        <Projects>
          <ProjectsContentBox>
            <Title
              text="projekty"
              size={68}
              color={colors(0.2).navy3}
              weight={700}
            />
            <InfoBoxWithMenu
              data={state}
              handleClick={handleClick}
              text={text}
              active={active}
            />
          </ProjectsContentBox>
          <Paragraph text={text} />
        </Projects>
      </Bounce>
    </Box>
  );
}

export default AboutDetails;
