import React from 'react';
import styled from 'styled-components';
import testimonials from '../../assets/testimonials.jpg';
import { colors } from '../../styles';

const TestimonialsBox = styled.div`
  min-height: 300px;
  background-image: ${(props) => `url(${props.bgc})`};
  background-position: center;
  background-size: cover;
  filter: saturate(10);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: ${colors(0.5).violet};
  }
`;

function Testimonials() {
  return <TestimonialsBox bgc={testimonials}></TestimonialsBox>;
}

export default Testimonials;
