import React, { useState } from 'react';
import styled from 'styled-components';
import { colors } from '../../styles';
import VideoIntro from './VideoIntro';

const Box = styled.div`
  height: ${(props) => (props.height ? '300px' : '80px')};
  overflow-y: ${(props) => (props.height ? 'scroll' : 'hidden')};
  border-bottom: 1px solid ${colors().darkGrey};
  transition: all 0.3s ease-out;
  background-color: ${(props) =>
    props.height ? `${colors(0.05).navy1}` : `${colors(0.15).grey}`};
  /* background-color: ${colors(0.15).orange}; */
`;
const Heading = styled.div`
  padding: 30px 5px;
  /* height: ${(props) => (props.height ? '10%' : '100%')}; */
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: ${colors().navy1};
  h3 {
    font-size: 22px;
  }
`;
const Main = styled.div`
  padding: 0 0 20px;

  p {
    font-size: 20px;
    line-height: 1.4;
    padding-bottom: 20px;
  }
`;

function VideoItem() {
  const [isOpened, setOpened] = useState(false);

  return (
    <Box height={isOpened}>
      <Heading onClick={() => setOpened((prev) => !prev)}>
        <h3>Tytu≈Ç tej sekcji</h3>
        <i className="fa fa-chevron-down"></i>
      </Heading>
      <Main>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Quia sequi
          veniam tempora animi hic velit iusto obcaecati exercitationem
          accusamus quisquam.
        </p>
        <iframe
          width="350"
          height="220"
          src="https://www.youtube.com/embed/PUGW7ehLrww"
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </Main>
    </Box>
  );
}

export default VideoItem;
