import React from 'react';
import styled from 'styled-components';
import { colors } from '../../styles';
import Container from '../Container';
import Title from '../Title';
import AboutDetails from './AboutDetails';
import AboutIntro from './AboutIntro';

const Box = styled.div`
  background-color: ${colors().grey};
  min-height: 200px;
  height: 400px;
  padding: 40px 0;
  line-height: 1.3;
  position: relative;
  overflow: hidden;
  /* z-index: 100; */

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 50px;
    background-color: ${colors().grey};

    z-index: 10;
  }

  /* > div:nth-of-type(2) {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translate(-50%, 50%) rotate(45deg);
    width: 50px;
    height: 50px;
    background-color: ${colors().orange};
    box-shadow: 0 0 30px -1px ${colors().orange};
    z-index: 10;
  } */

  div > span {
    font-size: 20px;
    color: ${colors().navy1};
    display: inline-block;
    padding: 10px 0;
  }
`;

function About() {
  return (
    <Box>
      <Container>
        <AboutIntro />
        <AboutDetails />
      </Container>
      <div></div>
    </Box>
  );
}

export default About;
