import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

import Item from './Item';
import ItemsBox from './ItemsBox';
import { breakpoints, colors } from '../../styles';

const SliderBox = styled.div`
  /* border: 5px solid white; */
  max-width: 100%;
  margin: 0 auto;
  position: relative;

  @media ${breakpoints('xsm')} {
    max-width: 90%;
  }
`;

const Arrows = styled.div`
  position: absolute;
  height: 50%;
  left: -10px;
  right: -10px;
  transform: translateY(85%);
  z-index: 100;
  display: flex;
  justify-content: space-between;

  i {
    font-size: 45px;
    height: max-content;
    color: ${colors().navy1};
    cursor: pointer;
    transition: all 0.2s;

    &:hover {
      text-shadow: 0 1px 10px ${colors().orange};
    }
  }
`;

function Slider({ data }) {
  const [state, setState] = useState(data);
  let overFlowContainer;
  let item;
  let width;

  const handleLeftClick = (arr) => {
    arr.shift();
    // setState(() => [...arr]);
    width = item.clientWidth + item.offsetLeft * 2;
    overFlowContainer.style.transform = `translateX(-${width}px)`;
  };
  const handleRightClick = (e) => {};

  useEffect(() => {
    overFlowContainer = document.querySelector('.overflow');
    item = document.querySelector('.overflow div');

    console.log(width);
  }, [state]);

  return (
    <SliderBox>
      <Arrows>
        <i
          className="fa fa-chevron-left"
          onClick={() => {
            handleLeftClick(state);
          }}
        ></i>
        <i className="fa fa-chevron-right" onClick={handleRightClick}></i>
      </Arrows>
      <ItemsBox data={state} />
    </SliderBox>
  );
}

export default Slider;
