import React, { useEffect, useState } from 'react';
import { Zoom } from 'react-reveal';
import styled from 'styled-components';
import { breakpoints, colors } from '../../styles';
import Container from '../Container';
import Testimonials from '../testimonials/Testimonials';
import Title from '../Title';
import axios from 'axios';

const Box = styled.div`
  min-height: 300px;
  padding: 50px 0;
  background-color: ${colors().grey}; ;
`;

const MainBox = styled.div`
  padding: 40px 0;
`;
const Table = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  width: 90%;
  margin: 0 auto;
`;
const Item = styled.div`
  width: 100%;
  font-size: 24px;
  padding: 10px;

  @media ${breakpoints('md')} {
    width: 50%;
  }

  &:nth-child(even) {
    /* text-align: end; */
  }

  i {
    color: ${colors().orange};
    padding-right: 10px;
  }
`;
const Description = styled.div`
  padding: 40px 0;
  width: 80%;
  margin: 0 auto;
`;

const Par = styled.div`
  font-size: 22px;
  line-height: 1.4;
  padding: 10px 0;
`;

const List = styled.ol``;

const Gallery = styled.div``;

function Training() {
  const [training, setTraining] = useState([]);
  const [gallery, setGallery] = useState([]);

  useEffect(async () => {
    const trainings = await axios('/api/trainings');
    const imgs = await axios('/api/gallery');

    setTraining(trainings.data.data.training[0]);
    setGallery(imgs.data.data);
  }, []);

  console.log(gallery);

  return (
    <Box>
      <Container>
        <Zoom>
          <Title text="Szkolenia" size={38} after={true} />
          <MainBox>
            <Table>
              <Item>
                <i className="fas fa-pen-nib"></i>
                <span>{training.title}</span>
              </Item>
              <Item>
                <i className="fas fa-users"></i>
                <span>{training.target}</span>
              </Item>
              <Item>
                <i className="far fa-clock"></i>
                <span>{training.date}</span>
              </Item>
              <Item>
                <i className="fas fa-map-marker-alt"></i>
                <span>{training.address}</span>
              </Item>
            </Table>
            <Description>
              <Title text="Opis i agenda" />
              <Par>{training.description}</Par>
              <List>
                {training.agenda &&
                  training.agenda.map((el) => (
                    <li key={el.id}>
                      {el.hour}: {el.content}
                    </li>
                  ))}
              </List>
            </Description>
            <Gallery>
              <Title text="Galeria" />
            </Gallery>
          </MainBox>
        </Zoom>
        <Testimonials />
      </Container>
    </Box>
  );
}

export default Training;
