import React, { useState } from 'react';
import styled from 'styled-components';
import { colors } from '../../styles';
import Container from '../Container';
import AboutDetails from './AboutDetails';
import AboutIntro from './AboutIntro';

const Box = styled.div`
  background-color: ${colors().grey};
  min-height: 200px;
  /* height: 400px; */
  padding: 40px 0;
  line-height: 1.3;
  position: relative;
  /* overflow: hidden; */
  /* z-index: 100; */

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 50px;
    background-color: ${colors().grey};

    z-index: 10;
  }

  > div:nth-of-type(2) {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translate(-50%, 50%) rotate(45deg);
    width: 50px;
    height: 50px;
    background-color: ${colors().grey};
    box-shadow: 0 0 30px -1px ${colors().orange};
    z-index: 10;
  }
`;

function About() {
  const [state, setState] = useState({ isOpened: false });

  const handleClick = (e) => {
    setState((prev) => {
