import React, { useState } from 'react';
import styled from 'styled-components';
import { colors } from '../../styles';
import Container from '../Container';
import Title from '../Title';

const Box = styled.div`
  padding: 40px 0;

  > div h3 {
    text-align: center;
    margin-top: 40px;
  }
`;

const InvestorsBox = styled.div`
  padding: 40px 0;
`;

const InvestorItem = styled.div`
  width: 85%;
  margin: 25px auto;
  line-height: 1.3;
  color: ${colors().darkGrey};
  /* background-color: ${colors(0.1).grey}; */
  background: rgb(15, 32, 39);
  background: linear-gradient(
    180deg,
    ${colors(0.75).navy2} 0%,
    ${colors(0).navy1} 100%
  );
  box-shadow: 0 0 12px -2px ${colors().navy2};
  position: relative;
  font-size: 18px;
  /* border-radius: 0px 10px 10px 10px; */

  &.topLeft {
    border-radius: 0 20px 20px 20px;
    left: -8px;
    padding: 15px 15px 15px 30px;

    &::before {
      top: -15%;
      left: -12%;
    }
  }

  &.topRight {
    border-radius: 20px 0 20px 20px;
    right: -8px;
    text-align: right;
    padding: 15px 30px 15px 15px;

    &::before {
      top: -15%;
      right: -12%;
    }
  }

  &.bottomLeft {
    border-radius: 20px 20px 20px 0;
    left: -8px;
    padding: 15px 15px 15px 30px;

    &::before {
      bottom: -15%;
      left: -12%;
    }
  }

  &.bottomRight {
    border-radius: 20px 20px 0 20px;
    right: -8px;
    text-align: right;
    padding: 15px 30px 15px 15px;

    &::before {
      bottom: -15%;
      right: -12%;
    }
  }

  &::before {
    content: '${(props) => `${props.id}`}';
    position: absolute;

    font-family: 'Ubuntu', sans-serif;
    font-size: 112px;
    font-weight: 700;
    color: ${colors(0.18).violet};
  }

  h4 {
    font-size: 20px;
    font-weight: 700;
    color: ${colors().navy1};
    padding: 5px 0;
  }
`;

function Investors() {
  const [state] = useState([
    {
      id: 1,
      name: 'Pośrednik ds. nieruchomości',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'topLeft',
    },
    {
      id: 2,
      name: 'Inwestor',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'topRight',
    },
    {
      id: 3,
      name: 'Deweloper',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'bottomLeft',
    },
    {
      id: 4,
      name: 'Flipper',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'bottomRight',
    },
  ]);
  return (
    <Box>
      <Container>
        <Title
          size={38}
          color={colors().grey}
          text="Kto zarabia na nieruchomościach"
          after={true}
        />
        <InvestorsBox>
          {state.map((el) => (
            <InvestorItem
              id={el.id}
              data-id={el.id}
              key={el.id}
              className={el.type}
            >
              <h4>{el.name}</h4>
              <span>{el.text}</span>
            </InvestorItem>
          ))}
        </InvestorsBox>
      </Container>
    </Box>
  );
}

export default Investors;
