import React, { useState } from 'react';
import styled from 'styled-components';
import { colors } from '../../styles';
import Container from '../Container';
import Title from '../Title';

const Box = styled.div`
  padding: 40px 0;

  > div h3 {
    text-align: center;
    margin-top: 40px;
  }
`;

const InvestorsBox = styled.div`
  padding: 40px 0;
`;

const InvestorItem = styled.div`
  width: 80%;
  margin: 25px auto;
  padding: 15px 15px 15px 35px;
  background-color: ${colors(0.1).grey};
  box-shadow: 0 0 12px -2px ${colors().navy2};
  position: relative;
  /* border-radius: 0px 10px 10px 10px; */
  border-top-left-radius: ${(props) =>
    props.type === 'topLeft' ? '0px' : '10px'};

  &::after {
    content: '${(props) => `${props.id}`}';
    position: absolute;
    top: -15%;
    left: -10%;
    font-family: 'Ubuntu', sans-serif;
    font-size: 112px;
    font-weight: 700;
    color: ${colors(0.25).violet};
  }
`;

function Investors() {
  const [state] = useState([
    {
      id: 1,
      name: 'Pośrednik ds. nieruchomości',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'topLeft',
    },
    {
      id: 2,
      name: 'Inwestor',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'topRight',
    },
    {
      id: 3,
      name: 'Deweloper',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'bottomLeft',
    },
    {
      id: 4,
      name: 'Flipper',
      text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam vel ligula mi. Pellentesque eu odio ac nunc mollis lobortis. Aenean nec urna ante. In molestie nunc ut dapibus mollis. Aliquam tristique ultrices elit aliquet posuere. Vestibulum aliquet tincidunt nulla at mollis.',
      type: 'bottomRight',
    },
  ]);
  return (
    <Box>
      <Container>
        <Title
          size={38}
          color={colors().grey}
          text="Kto zarabia na nieruchomościach"
          after={true}
        />
        <InvestorsBox>
          {state.map((el) => (
            <InvestorItem id={el.id} data-id={el.id} key={el.id} type={el.type}>
              <h4>{el.name}</h4>
              <span>{el.text}</span>
            </InvestorItem>
          ))}
        </InvestorsBox>
      </Container>
    </Box>
  );
}

export default Investors;
