import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Container from './Container';
import { Link } from 'react-router-dom';
import { breakpoints, colors } from '../styles';
import { useSelector, useDispatch } from 'react-redux';
import { changeNav } from '../redux/mobileNavSlice';

const Head = styled.header`
  height: 120px;
  position: relative;

  > div {
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: 100%;
  }

  @media ${breakpoints().sm} {
    height: 130px;
  }

  @media ${breakpoints().md} {
    height: 150px;
  }
`;

const Logo = styled.div`
  z-index: 100;
  width: 40px;
  line-height: 0.7;
  a {
    color: ${colors().orange};
    font-weight: 700;
    font-size: 22px;

    @media ${breakpoints().sm} {
      font-size: 24px;
    }

    @media ${breakpoints().md} {
      font-size: 28px;
    }

    @media ${breakpoints().lg} {
      font-size: 32px;
    }
  }
`;
const Menu = styled.div`
  display: none;
  flex-grow: 1;

  @media ${breakpoints().md} {
    display: block;
  }
  nav {
    ul {
      display: flex;
      justify-content: flex-end;

      li {
        margin: 0 6px;
        a {
          font-size: 20px;
          display: inline-block;
          width: 100%;
          height: 100%;
          color: ${colors(0.75).grey};
          padding: 5px 10px;

          @media (hover: hover) {
            &:hover {
              color: ${colors().orange};
            }
          }
        }
        &:nth-of-type(4) {
          a {
            border-radius: 2px;
            background-color: ${colors().orange};
            color: ${colors().navy2};
          }
        }
      }
    }
  }
`;

const MobileMenuBox = styled.div`
  width: 100%;
  height: 100%;

  @media ${breakpoints().md} {
    display: none;
  }
`;
const MobileMenu = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  transform: ${(props) =>
    props.isOpened ? 'translateX(0)' : 'translateX(100%)'};
  background: ${colors().navy2};
  background: linear-gradient(
    180deg,
    ${colors().navy1} 0%,
    ${colors().navy3} 100%
  );
  z-index: 100;
  transition: all 0.5s;

  @media ${breakpoints().md} {
    display: none;
  }

  nav {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -55%);

    ul {
      li {
        a {
          display: inline-block;
          width: 100%;
          padding: 25px;
          text-align: center;
          font-size: 38px;
          color: ${colors().orange};
        }
      }
    }
  }
`;

const Hamburger = styled.div`
  width: 40px;
  height: 30px;
  cursor: pointer;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  right: 5%;
  z-index: 1000;

  display: flex;
  flex-direction: column;
  justify-content: space-between;
  div {
    display: inline-block;
    width: 100%;
    height: 3px;
    background-color: ${colors().orange};
    border-radius: 20px;
    transition: all 0.5s;
  }

  div:nth-child(1) {
    transform: ${(props) =>
      props.isOpened
        ? 'rotate(135deg) translate(11px, -9px)'
        : 'rotate(0) translate(0, 0)'};
  }

  div:nth-child(2) {
    transform: ${(props) =>
      props.isOpened ? 'translateX(300%)' : 'translateX(0)'};
  }

  div:nth-child(3) {
    transform: ${(props) =>
      props.isOpened
        ? 'rotate(-135deg) translate(10px, 8px)'
        : 'rotate(0) translate(0, 0)'};
  }

  @media ${breakpoints().md} {
    display: none;
  }
`;

function Header() {
  const [isOpened, setOpened] = useState(false);
  const mobileNav = useSelector((state) => state.mobileNav.value);
  const dispatch = useDispatch();

  const handleOpenMenu = (e) => {
    setOpened((prev) => !prev);
    dispatch(changeNav());
  };

  useEffect(() => {
    const body = document.querySelector('body');
    body.style.overflow = mobileNav ? 'hidden' : 'auto';
  }, [mobileNav]);

  return (
    <Head>
      <Container>
        <Logo>
          <a href="/">krzysztof koziol</a>
        </Logo>
        <Menu>
          <nav>
            <ul>
              <li>
                <Link to="/wideo">wideo</Link>
              </li>
              <li>
                <Link to="/konsultacje">konsultacje</Link>
              </li>
              <li>
                <Link to="/szkolenia">szkolenia</Link>
              </li>
              <li>
                <a href="tel:+48-730-173-031">kontakt</a>
              </li>
            </ul>
          </nav>
        </Menu>
        <MobileMenuBox>
          <Hamburger isOpened={isOpened} onClick={handleOpenMenu}>
            <div></div>
            <div></div>
            <div></div>
          </Hamburger>
          <MobileMenu isOpened={isOpened}>
            <nav>
              <ul>
                <li>
                  <Link onClick={handleOpenMenu} to="/wideo">
                    wideo
                  </Link>
                </li>
                <li>
                  <Link onClick={handleOpenMenu} to="/konsultacje">
                    konsultacje
                  </Link>
                </li>
                <li>
                  <Link onClick={handleOpenMenu} to="/szkolenia">
                    szkolenia
                  </Link>
                </li>
                <li>
                  <a onClick={handleOpenMenu} href="tel:+48-730-173-031">
                    kontakt
                  </a>
                </li>
              </ul>
            </nav>
          </MobileMenu>
        </MobileMenuBox>
      </Container>
    </Head>
  );
}

export default Header;
