import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import Spinner from '../../Spinner';
import { colors } from '../../styles';
import Title from '../Title';

const Box = styled.div``;

const FormContainer = styled.form`
  width: 90%;
  margin: 0 auto;
  padding: 30px 0;
  max-width: 250px;
  display: flex;
  flex-direction: column;
  justify-content: center;

  input[type='submit'] {
    outline: none;
    border: none;
    width: 100%;
    background-color: ${colors().violet};
    color: ${colors().grey};
    font-size: 26px;
    font-weight: 700;
    text-align: center;
    text-transform: uppercase;
    padding: 15px;
    margin-top: 30px;
    border-radius: 12px;
    box-shadow: 0 0 12px -5px ${colors().navy1};
    cursor: pointer;
    transition: all 0.2s;

    @media (hover: hover) {
      &:hover {
        transform: scale(1.03);
        box-shadow: 0 0 15px -5px ${colors().navy1};
      }
    }
  }
`;

const Switch = styled.label`
  position: relative;
  display: inline-block;
  width: 60px;
  height: 34px;

  input {
    opacity: 0;
    width: 0;
    height: 0;
  }

  span {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: 34px;
    background-color: #ccc;
    -webkit-transition: 0.4s;
    transition: 0.4s;

    &::before {
      position: absolute;
      content: '';
      height: 26px;
      width: 26px;
      left: 4px;
      bottom: 4px;
      background-color: ${(props) =>
        props.toggle ? `${colors().orange}` : `${colors().violet}`};
      transform: ${(props) =>
        props.toggle ? 'translateX(26px)' : 'translateX(0)'};
      border-radius: 50%;
      -webkit-transition: 0.4s;
      transition: 0.4s;
    }
  }
`;

const Input = styled.div``;

function Form({ isLoading, getFromOffset, isMounted }) {
  const [state, setState] = useState({
    name: '',
    adress1: '',
    adress2: '',
    adress3: '',
    email: '',
  });
  const [toggle, setToggle] = useState(false);
  const ref = useRef();

  useEffect(() => {
    console.dir(ref.current.offsetTop);
    getFromOffset(ref.current);
  }, []);
  const handleChange = (e) => {
    setState((prev) => ({
      [e.target.id]: e.target.value,
    }));
  };

  const toggleSwitch = (e) => {
    console.log('toggle');
    setToggle((prev) => !prev);
  };
  return (
    <Box ref={ref}>
      <Title text="Dane kontaktowe" />
      <FormContainer
        // onSubmit={handleSubmit}
        action="/api/kup-szkolenie"
        // method="POST"
      >
        <Switch toggle={toggle}>
          <input
            type="checkbox"
            name="switch"
            id="switch"
            onChange={toggleSwitch}
            checked={toggle}
          />
          <span></span>
        </Switch>
        <div>
          <input
            type="text"
            name="name"
            id="name"
            value={state.name}
            onChange={handleChange}
          />
          <label htmlFor="name">Nazwa firmy</label>
        </div>
        {/* <div>
          <input type="checkbox" name="check" />
          <label htmlFor="check">
            {' '}
            Kupując szkolenie wyrażam zgodę na przetwarzanie moich danych
            osobowych.Zasady przetwarzania danych osobowych znajdziesz w{' '}
            <Link to="/polityka-prywatnosci">polityce prywatności</Link>.
          </label>
        </div> */}
        <div>
          {isLoading ? (
            // <Spinner>
            <Spinner />
          ) : (
            // </Spinner>
            <input type="submit" value="Kup szkolenie" />
          )}
        </div>
      </FormContainer>
    </Box>
  );
}

export default Form;
