import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ItemsBox from './ItemsBox';
import { breakpoints, colors } from '../../styles';
import koziol1 from '../../assets/koziol1.png';
import SliderSlick from 'react-slick';
import Slider2 from 'infinite-react-carousel';
import Item from './Item';
import { v4 as uuidv4 } from 'uuid';

const SliderBox = styled.div`
  /* border: 5px solid white; */
  max-width: 100%;
  height: min-content;
  margin: 0 auto;
  padding: 10px 0;
  position: relative;
  overflow-x: hidden;

  @media ${breakpoints('xsm')} {
    max-width: 90%;
  }
`;

const Arrows = styled.div`
  position: absolute;
  height: 50%;
  left: -10px;
  right: -10px;
  transform: translateY(85%);
  z-index: 100;
  display: flex;
  justify-content: space-between;

  i {
    font-size: 45px;
    height: max-content;
    color: ${colors().navy1};
    cursor: pointer;
    transition: all 0.2s;

    &:hover {
      text-shadow: 0 1px 10px ${colors().orange};
    }
  }
`;

function Slider({ data }) {
  const [state, setState] = useState(data);
  const [width, setWidth] = useState();

  return (
    <Slider2 slidesToShow={4} autoplay={true} className="slider">
      {state &&
        state.map((el) => <Item key={uuidv4()} data={el} width={width} />)}
    </Slider2>
  );
}

export default Slider;
