import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ItemsBox from './ItemsBox';
import { breakpoints, colors } from '../../styles';
import koziol1 from '../../assets/koziol1.png';
import SliderSlick from 'react-slick';

const SliderBox = styled.div`
  /* border: 5px solid white; */
  max-width: 100%;
  margin: 0 auto;
  position: relative;

  @media ${breakpoints('xsm')} {
    max-width: 90%;
  }
`;

const Arrows = styled.div`
  position: absolute;
  height: 50%;
  left: -10px;
  right: -10px;
  transform: translateY(85%);
  z-index: 100;
  display: flex;
  justify-content: space-between;

  i {
    font-size: 45px;
    height: max-content;
    color: ${colors().navy1};
    cursor: pointer;
    transition: all 0.2s;

    &:hover {
      text-shadow: 0 1px 10px ${colors().orange};
    }
  }
`;

function Slider() {
  const data = [
    {
      id: 1,
      name: 'Grzegorz Borowski',
      img: koziol1,
      company: 'Wawel Apartments',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
    {
      id: 2,
      name: 'Magdalena Makuch',
      img: koziol1,
      company: 'Record Consultings',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
    {
      id: 3,
      name: 'Mariusz Mariusz',
      img: koziol1,
      company: 'Doctor QBud',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
    {
      id: 4,
      name: 'Dominika Dominika',
      img: koziol1,
      company: 'Inspektor budowy',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
    {
      id: 5,
      name: 'Wojciech Matuszny',
      img: koziol1,
      company: 'Eco Masuria',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
    {
      id: 6,
      name: 'Ten ZeSzkolenia',
      img: koziol1,
      company: 'Ubezpieczenia',
      url: '#',
      text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis cupiditate perspiciatis animi? Quis accusamus sed aliquid quo corrupti, tempora necessitatibus?',
    },
  ];
  const [state, setState] = useState(data);
  let [load, setLoad] = useState(false);

  const handleLeftClick = (arr) => {
    let last = state.slice(-1);
    let rest = state.slice(0, -1);
    let images = [...last, ...rest];

    setState(images);
  };
  const handleRightClick = (e) => {
    console.log('right');
    // let [first, ...rest] = state;
    // let images = [...rest, first];
    cloneLastToFirst();
  };

  const cloneLastToFirst = () => {
    const first = Object.assign({}, state[state.length - 1]);
    setState((prev) => [first, ...prev]);
  };

  useEffect(() => {
    cloneLastToFirst();
    setLoad(true);
  }, []);

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <SliderBox>
      <Arrows>
        <i className="fa fa-chevron-left" onClick={handleLeftClick}></i>
        <i className="fa fa-chevron-right" onClick={handleRightClick}></i>
      </Arrows>
      {load && <ItemsBox data={state} />}
    </SliderBox>
  );
}

export default Slider;
